<?php
$dir = __DIR__ . '/posts/';
$files = glob($dir . '*.md');
rsort($files); // 新しい順

$perPage = 20;
$totalFiles = count($files);
$totalPages = ceil($totalFiles / $perPage);
$currentPage = isset($_GET['page']) ? max(1, (int)$_GET['page']) : 1;

// 現在ページに表示するファイルだけ取得
$start = ($currentPage - 1) * $perPage;
$filesToShow = array_slice($files, $start, $perPage);

// --- 記事リスト表示 ---
echo "<ul class='c-entryList'>";
foreach ($filesToShow as $file) {
    $content = file_get_contents($file);

    preg_match('/title:\s*(.+)/', $content, $m1);
    preg_match('/date:\s*(.+)/', $content, $m2);
    preg_match('/lead:\s*(.+)/', $content, $m4);

    $title = $m1[1] ?? basename($file);
    $date  = $m2[1] ?? '';
    $lead  = $m4[1] ?? '';
    $dt = new DateTime($date);
    $jpDate = $dt->format('Y年m月d日');

    $id = urlencode(basename($file));
    echo <<<HTML
    <li class='c-entryList__item'>
      <a href='view.php?id=$id' class='c-entryList__link'>
        <div class='c-entryList__date'>
          <time class='c-entryList__dateTime' datetime='$date'>
            $jpDate
          </time>
        </div>
        <div class='c-entryList__body'>
          <div class='c-entryList__text'>
HTML;
    if($lead) echo  "<h4 class='c-heading__label'>$title</h4><p>$lead</p>";
    else echo "$title";
    echo <<<HTML
          </div>
          <svg class='c-entryList__icon icon' aria-hidden='true' role='img'>
            <use xlink:href='/assets/img/sprite.svg#chevron_right'></use>
          </svg>
        </div>
      </a>
    </li>
HTML;
}
echo "</ul>";

// --- ページネーション表示 ---
echo "<div class='c-pagination'>";

// 最初のページ
echo "<div class='c-pagination__item c-pagination__item--controller'>";
echo "<button type='button' aria-label='最初のページ' onclick='location.href=\"?page=1\"'>";
echo "<svg class='c-button__icon icon' aria-hidden='true' role='img'>";
echo "<use xlink:href='/studentsurvey/assets/img/sprite.svg#chevron-line_left'></use>";
echo "</svg></button></div>";

// 前へ
$prevPage = max(1, $currentPage - 1);
echo "<div class='c-pagination__item c-pagination__item--controller'>";
echo "<button type='button' aria-label='前へ' onclick='location.href=\"?page=$prevPage\"'>";
echo "<svg class='c-button__icon icon' aria-hidden='true' role='img'>";
echo "<use xlink:href='/studentsurvey/assets/img/sprite.svg#chevron_left'></use>";
echo "</svg></button></div>";

// セレクトボックス
echo "<div class='c-selectGroup'>";
echo "<select class='c-select' aria-label='表示するページ' onchange='location.href=\"?page=\"+this.value'>";
for ($i = 1; $i <= $totalPages; $i++) {
    $selected = ($i === $currentPage) ? "selected" : "";
    echo "<option value='$i' $selected>$i / $totalPages</option>";
}
echo "</select></div>";

// 次へ
$nextPage = min($totalPages, $currentPage + 1);
echo "<div class='c-pagination__item c-pagination__item--controller'>";
echo "<button type='button' aria-label='次へ' onclick='location.href=\"?page=$nextPage\"'>";
echo "<svg class='c-button__icon icon' aria-hidden='true' role='img'>";
echo "<use xlink:href='/studentsurvey/assets/img/sprite.svg#chevron_right'></use>";
echo "</svg></button></div>";

// 最後のページ
echo "<div class='c-pagination__item c-pagination__item--controller'>";
echo "<button type='button' aria-label='最後のページ' onclick='location.href=\"?page=$totalPages\"'>";
echo "<svg class='c-button__icon icon' aria-hidden='true' role='img'>";
